import React, { Component } from "react";
import { render } from "react-dom";
import {
  Button,
  Form,
  FormGroup,
  Label,
  Input,
  FormText,
  ButtonToggle,
  Container,
  Col
} from "reactstrap";
import "./home.scss";
import NavBar from "../../components/Navbar/Navbar";
import { ToastContainer, toast } from "react-toastify";
function triggerSuccess() {
  toast.success("Wow ðŸš€");
}
const Home = () => {
  return (
    <div className="home">
      <button onClick={triggerSuccess}>Trigger Success</button>
      <ToastContainer closeButton={<CloseButton />} />
      Home
    </div>
  );
};

export default Home;

function CloseButton({ closeToast, type, ariaLabel }) {
  const styles = css({
    color: "#000",
    fontWeight: "bold",
    fontSize: "30px",
    background: "transparent",
    outline: "none",
    border: "none",
    padding: 0,
    cursor: "pointer",
    opacity: "0.7",
    transition: ".3s ease",
    alignSelf: "flex-start",
    ":hover, :focus": {
      opacity: 1
    }
  });
  return (
    <button
      {...styles}
      type="button"
      onClick={closeToast}
      aria-label={ariaLabel}
    >
      âœ–
    </button>
  );
}
