import { ADD_TO_CART, REMOVE_FROM_CART } from "./actions/types";
const initialState = { items: [] };

function Cart(state = initialState, action) {
  switch (action.type) {
    case ADD_TO_CART:
      return {
        ...state,
        items: action.payload.cartItems,
        filteredItems: action.payload
      };

    default:
      return state;
  }
}

export default Cart;
