const initialState = {
  email: "",
  password: ""
};

function auth(state = initialState, action) {
  switch (action.type) {
    case "LOGIN_USER":
      return {
        ...state,
        ...action.payload
      };
    default:
      return state;
  }
}

export default auth;

// Actions
export const authAction = user => ({
  type: "LOGIN_USER",
  payload: user
});
